//span[@id='imgarea']/a/img/@src##//##https://@js:

// 获取前缀URL
const regexStr = "^https://img5\\.qy0\\.ru/data/\\d+/\\d+/";
const regex = new RegExp(regexStr, "g");
const prefixUrlMatch = result.match(regex);
const prefixUrl = prefixUrlMatch ? prefixUrlMatch[0] : "";

// 解析图片信息数组（容错：数组为空时设为[]）
const imgInfoList = JSON.parse(java.get('imgInfoList') || '[]');

// 拼接完整图片链接（直接使用原始imgName）
const imgUrlList = [];
if (prefixUrl) {
    imgInfoList.forEach(item => {
        // 仅做空值容错，不修改imgName原始值
        const imgName = item.imgName || '';
        const imgExtension = item.imgExtension || 'jpg';
        const fullImgUrl = prefixUrl + imgName + "." + imgExtension;
        imgUrlList.push(fullImgUrl);
    });
}

// 规则输出拼接后的img标签
imgUrlList.map(x=>'<img src=\"'+x+'\">')
.join("\n")

