//div[@class='gallary_wrap tb']/ul/li[1]@js:
var imgInfoList = [];
var nextPageUrl = [];

// 1. 处理分页URL（保留有效逻辑）
var rawHrefs = java.getString("//div[@class='f_left paginator']/a/@href") || "";
nextPageUrl = rawHrefs.split('\n').filter(x => x.trim());
nextPageUrl = nextPageUrl.map(x => {
    var url = x.trim();
    while (url.startsWith("/")) {
        url = url.slice(1);
    }
    return url.startsWith("http") ? url : "https://www.wnacg.ru/" + url;
});
nextPageUrl.unshift(baseUrl);

// 2. 核心工具函数：预处理HTML（移除注释+清理空格）
function cleanHtml(html) {
    if (!html) return "";
    // 步骤1：移除所有HTML注释（<!-- ... -->），彻底消除干扰
    html = html.replace(/<!--[\s\S]*?-->/g, "");
    // 步骤2：移除多余空白字符（换行、制表符等），简化匹配
    html = html.replace(/\s+/g, " ");
    return html;
}

// 3. 核心工具函数：提取单页的“序号数组”和“图片src数组”
function extractPageData(cleanedHtml) {
    var names = []; // 序号数组（001、002、003...）
    var srcs = [];  // 图片src数组（与序号一一对应）

    // 提取所有序号（<span class="name tb">xxx</span>）
    var nameRegex = /<span class="name tb">([\d]+)<\/span>/g;
    var nameMatch;
    while ((nameMatch = nameRegex.exec(cleanedHtml)) !== null) {
        names.push(nameMatch[1]);
    }

    // 提取所有有效图片src（与序号对应的img，alt包含“_序号”）
    var srcRegex = /<img src="([^"]+)" alt="[^"]+_([\d]+)"/g;
    var srcMatch;
    while ((srcMatch = srcRegex.exec(cleanedHtml)) !== null) {
        // 确保src对应的序号在names数组中（避免无关图片）
        if (names.includes(srcMatch[2])) {
            srcs.push(srcMatch[1]);
        }
    }

    return { names: names, srcs: srcs };
}

// 4. 执行多页提取
try {
    for (var p = 0; p < nextPageUrl.length; p++) {
        var pageUrl = nextPageUrl[p];
        var pageNum = p + 1;

        // 步骤1：获取并清理HTML（移除注释+多余空格）
        var rawHtml = new String(java.ajax(pageUrl)) || "";
        var cleanedHtml = cleanHtml(rawHtml);

        // 步骤2：提取当前页的序号和src数组
        var pageData = extractPageData(cleanedHtml);
        var pageNames = pageData.names;
        var pageSrcs = pageData.srcs;

        // 步骤3：按索引配对（序号和src一一对应），避免漏配
        var addedNum = 0;
        for (var i = 0; i < pageNames.length; i++) {
            var imgName = pageNames[i];
            var imgSrc = pageSrcs[i] || "";
            var imgExtension = "";

            // 提取扩展名
            if (imgSrc) {
                var dotIndex = imgSrc.lastIndexOf(".");
                if (dotIndex !== -1) {
                    imgExtension = imgSrc.slice(dotIndex + 1).toLowerCase().split(/[?#]/)[0];
                }
            }

            // 去重并添加到数组
            if (imgName && imgExtension && !imgInfoList.some(item => item.imgName === imgName)) {
                imgInfoList.push({ imgName: imgName, imgExtension: imgExtension });
                addedNum++;
            }
        }

    }
} catch (e) {
    java.log("提取报错：" + e.message);
}

imgInfoList.sort((a, b) => parseInt(a.imgName) - parseInt(b.imgName));

// 返回原结果，不影响目录列表
java.put('imgInfoList',JSON.stringify(imgInfoList))
result;

