var host = [
    'http://219.154.201.122:5006'
];

function getArguments(open_argument, key) {
    try {
        open_argument = JSON.parse(open_argument);
    } catch (e) {
        open_argument = {
            media: "小说",
            tone_id: "默认音色",
            server: host[0],
            source: "全部",
            source_type: "男频",
        };
    }
    if (key) {
        return open_argument[key];
    } else {
        return open_argument;
    }
}

function setArguments(key, value) {
    const {
        source
    } = this;
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument, '');
    open_argument[key] = value;
    open_argument = JSON.stringify(open_argument);
    source.setVariable(open_argument);
    return open_argument;
}

function decrypt(Text) {
    return Text;
}

function paraForAndroid(content, sources) {
    let {
        java,
        cache,
        source
    } = this;
    let plcolor = getArguments(source.getVariable(), "plcolor");
    if (!plcolor) {
        plcolor = "#000000";
    }

    const createSvg = this.createSvg.bind(this);

    return content.replace(/<p>(.*?)(?:<comment ident="([^"]*)" count="([^"]*)" \/>)?<\/p>/g,
        (match, text, url, count) => {
            if (url && count) {
                const click = 0;
                cache.putMemory(url, click);
                const encodedUrl = url;
                return `<p>${text}<img src="${createSvg(count, plcolor,encodedUrl,sources)}"></p>`;
            } else {
                return `<p>${text}</p>`;
            }
        }
    );
}


function showCmt(url, sources) {
    let {
        java,
        cache
    } = this;

    const currentTime = Date.now();
    const click = cache.getFromMemory(url);
    let isqread = false;
    try {
        java.qread();
        isqread = true;
    } catch (e) {}
    if (click < 1 && !isqread) {
        cache.putMemory(url, click + 1);
        return;
    } else {
        try {
            java.startBrowserDp(url, sources + '段评');
        } catch (e) {
            java.startBrowser(url, sources + '段评');
        }
    }
}



function createSvg(number, color, encodedUrl, sources) {
    var displayText = number > 99 ? "99+" : number.toString();
    var loginInfoMap = {};

    if (this.source && typeof this.source.getLoginInfoMap == 'function') {
        loginInfoMap = this.source.getLoginInfoMap() || {};
    }

    var bubbleStyle = String(loginInfoMap['段评气泡样式'] || '0');
    var svg;

    // 样式1：精致圆形 - 简洁优雅
    if (bubbleStyle == '1') {
        svg = '<svg width="1000" height="1000" xmlns="http://www.w3.org/2000/svg">' +
            '<circle cx="500" cy="500" r="440" fill="none" stroke="' + color + '" stroke-width="32" opacity="0.2"/>' +
            '<circle cx="500" cy="500" r="440" fill="none" stroke="' + color + '" stroke-width="4"/>' +
            '<text x="500" y="500" font-family="-apple-system, sans-serif" text-anchor="middle" ' +
            'font-size="400" fill="' + color + '" dy="0.35em" font-weight="500">' + displayText + '</text>' +
            '</svg>';
    }
    // 样式2：微信风格 - 熟悉亲切的对话气泡
    else if (bubbleStyle == '2') {
        svg = '<svg width="1000" height="900" xmlns="http://www.w3.org/2000/svg">' +
            '<path d="M120,120 L880,120 Q920,120 920,160 L920,680 Q920,720 880,720 L280,720 L120,840 L120,720 Q80,720 80,680 L80,160 Q80,120 120,120 Z" ' +
            'fill="none" stroke="' + color + '" stroke-width="24" stroke-linejoin="round"/>' +
            '<text x="500" y="440" font-family="-apple-system, sans-serif" text-anchor="middle" ' +
            'font-size="360" fill="' + color + '" dy="0.32em" font-weight="500">' + displayText + '</text>' +
            '</svg>';
    }
    // 样式3：悬浮标签 - 扁平现代风格
    else if (bubbleStyle == '3') {
        svg = '<svg width="1000" height="600" xmlns="http://www.w3.org/2000/svg">' +
            '<rect x="80" y="80" width="840" height="440" rx="220" ry="220" ' +
            'fill="none" stroke="' + color + '" stroke-width="6"/>' +
            '<rect x="100" y="100" width="800" height="400" rx="200" ry="200" ' +
            'fill="none" stroke="' + color + '" stroke-width="20" opacity="0.3"/>' +
            '<text x="500" y="310" font-family="-apple-system, sans-serif" text-anchor="middle" ' +
            'font-size="280" fill="' + color + '" dy="0.32em" font-weight="500" letter-spacing="8">' + displayText + '</text>' +
            '</svg>';
    }
    // 样式4：书签标注 - 符合阅读场景
    else if (bubbleStyle == '4') {
        svg = '<svg width="900" height="1000" xmlns="http://www.w3.org/2000/svg">' +
            '<path d="M150,80 L750,80 Q800,80 800,130 L800,870 Q800,920 750,920 L480,920 L450,980 L420,920 L150,920 Q100,920 100,870 L100,130 Q100,80 150,80 Z" ' +
            'fill="none" stroke="' + color + '" stroke-width="24" stroke-linejoin="round"/>' +
            '<line x1="150" y1="280" x2="750" y2="280" stroke="' + color + '" stroke-width="3" opacity="0.3"/>' +
            '<text x="450" y="520" font-family="Georgia, serif" text-anchor="middle" ' +
            'font-size="360" fill="' + color + '" dy="0.32em" font-weight="400">' + displayText + '</text>' +
            '<text x="450" y="800" font-family="-apple-system, sans-serif" text-anchor="middle" ' +
            'font-size="70" fill="' + color + '" opacity="0.6" letter-spacing="3">评论</text>' +
            '</svg>';
    }
    // 默认返回样式0
    else {
        svg = '<svg width="160" height="120" xmlns="http://www.w3.org/2000/svg">' +
            '<path d="M 55 10 ' +
            'L 120 10 ' +
            'Q 150 10 150 40 ' +
            'L 150 80 ' +
            'Q 150 110 120 110 ' +
            'L 55 110 ' +
            'Q 25 110 25 80 ' +
            'L 25 75 ' +
            'L 3 60 ' +
            'L 25 45 ' +
            'L 25 40 ' +
            'Q 25 10 55 10 Z" ' +
            'fill="none" ' +
            'stroke="' + color + '" ' +
            'stroke-width="2" ' +
            'stroke-linejoin="round"/>' +
            '<!-- 数字文本 -->' +
            '<text x="87" y="75" ' +
            'font-family="Arial, sans-serif" ' +
            'text-anchor="middle" ' +
            'dominant-baseline="middle" ' +
            'font-size="50" ' +
            'font-weight="bold" ' +
            'fill="' + color + '">' +
            displayText +
            '</text>' +
            '<!-- 提示文本 -->' +
            '<text x="87" y="95" ' +
            'font-family="Arial, sans-serif" ' +
            'text-anchor="middle" ' +
            'dominant-baseline="middle" ' +
            'font-size="9" ' +
            'fill="' + color + '" ' +
            'opacity="0.7">' +
            '刷新查看' +
            '</text>' +
            '</svg>';
    }

    var encodedSvg = this.java.base64Encode(svg);
    return 'data:image/svg+xml;base64,' + encodedSvg + ',{"js":"showCmt(\'' + encodedUrl + '\', \'' + sources + '\')","style":"text"}';
}



function cleanHTML(html) {
    let result = html
        .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, "")
        .replace(/<div class="tt-title"[^>]*>[\s\S]*?<\/div>/gi, "")
        .replace(/<(?!\/?p\b|\/?img\b)[^>]+>/gi, "");
    result = result.replace(/<\/?p[^>]*>/g, "\n");
    return result.replace(/\n+/g, "\n").trim();
}

function getSessionId(cookieString) {
    const match = cookieString.match(/sessionid=([^;]+)/);
    return match ? match[1] : null;
}

function getKey(key) {
    let parts = key.split(";");
    for (let part of parts) {
        if (part.includes("qttoken")) {
            return part.split("=")[1];
        }
    }
    return "";
}

function getFanqieCookie() {
    const {
        cookie
    } = this;
    try {
        return String(cookie.getCookie('fanqienovel.com') || java.getCookie('fanqienovel.com') || '');
    } catch (e) {
        return '';
    }
}


function paraForiOS(html, sources) {
    return html.replace(
        /<p>(.*?)(?:<comment ident="([^"]*)" count="([^"]*)" \/>)?<\/p>/g,
        function(match, text, url, count) {
            if (url && count) {
                const encodedUrl = url.replace(/&/g, '&amp;');
                return `<span rs-native>${text}<comment count="${count}" onPress="java.showReadingBrowser('${encodedUrl}','${sources}段评')"></span>`;
            } else {
                return `<span rs-native>${text}</span>`;
            }
        }
    );
}

