      box-shadow: var(--glow-shadow), var(--shadow);
      color: #fff;
      z-index: 2;
      animation: gradientAnimation 6s ease infinite, breathe 3s ease-in-out infinite;
      position: relative;
      overflow: hidden;
    }

    .version-card.latest-version::before {
      content: '';
      position: absolute;
      top: -50%;
      left: -50%;
      width: 200%;
      height: 200%;
      background: linear-gradient(
        90deg,
        transparent,
        rgba(255, 255, 255, 0.3),
        transparent
      );
      transform: rotate(45deg);
      animation: shimmer 3s infinite;
    }

    .version-card.latest-version h3,
    .version-card.latest-version .version-number,
    .version-card.latest-version .version-date {
      color: #fff;
      text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
      position: relative;
      z-index: 1;
    }

    .version-status {
      position: absolute;
      top: 6px;
      right: 6px;
      padding: 3px 7px;
      font-size: 0.65rem;
      font-weight: 600;
      border-radius: 6px;
      color: #fff;
      line-height: 1.2;
      white-space: nowrap;
      z-index: 2;
    }

    .version-card.latest-version .version-status {
      background: rgba(255, 255, 255, 0.25);
      backdrop-filter: blur(5px);
      border: 1px solid rgba(255, 255, 255, 0.3);
      color: #fff;
      box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
    }

    .status-outdated { background: var(--warning-color); }
    .status-latest { background: var(--success-color); }
    .status-invalid { background: var(--error-color); }

    .version-card h3 {
      font-size: 0.9rem;
      color: var(--text-secondary);
      margin-bottom: 8px;
      font-weight: 500;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 6px;
    }

    .version-number {
      font-size: 1.25rem;
      font-weight: 700;
      color: var(--text-main);
      margin: 8px 0;
      transition: all 0.3s ease;
      font-family: 'Courier New', monospace;
    }

    .version-card.latest-version .version-number {
      font-size: 1.4rem;
      transform: scale(1.05);
      text-shadow: 
        0 2px 4px rgba(0, 0, 0, 0.3),
        0 0 10px rgba(212, 175, 55, 0.8),
        0 0 20px rgba(212, 175, 55, 0.5);
      animation: pulse-glow 2s ease-in-out infinite;
    }

    @keyframes pulse-glow {
      0%, 100% {
        text-shadow: 
          0 2px 4px rgba(0, 0, 0, 0.3),
          0 0 10px rgba(212, 175, 55, 0.8),
          0 0 20px rgba(212, 175, 55, 0.5);
      }
      50% {
        text-shadow: 
          0 2px 4px rgba(0, 0, 0, 0.3),
          0 0 15px rgba(212, 175, 55, 1),
          0 0 30px rgba(212, 175, 55, 0.7);
      }
    }

    .version-date {
      font-size: 0.8rem;
      color: var(--text-secondary);
      margin-top: 4px;
    }

    /* 版本对比指示器 */
    .version-indicator {
      position: absolute;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%);
      z-index: 5;
      width: 32px;
      height: 32px;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
      color: white;
    }

    .version-indicator.update-needed {
      background: var(--error-color);
      box-shadow: 0 2px 8px rgba(234, 84, 85, 0.4);
      animation: pulse-indicator 1.5s infinite;
    }

    .version-indicator.is-latest {
      background: var(--success-color);
      box-shadow: 0 2px 8px rgba(40, 199, 111, 0.4);
    }

    @keyframes pulse-indicator {
      0% { transform: translate(-50%, -50%) scale(1); }
      50% { transform: translate(-50%, -50%) scale(1.1); }
      100% { transform: translate(-50%, -50%) scale(1); }
    }

    /* 内容区 */
    .content-container {
      padding: 16px;
    }

    /* 状态提示 */
    .status-alert {
      background: var(--card-bg);
      border-radius: 16px;
      box-shadow: var(--shadow);
      margin-bottom: 16px;
      overflow: hidden;
      border: 1px solid rgba(120, 130, 240, 0.1);
      padding: 12px 16px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;
      font-weight: 500;
      font-size: 14px;
      animation: slideIn 0.5s ease 0.3s backwards;
    }

    .status-alert i {
      font-size: 20px;
    }

    .status-alert.update-available {
      background: linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%);
      color: #d63031;
      box-shadow: 0 4px 15px rgba(253, 203, 110, 0.4);
    }

    .status-alert.up-to-date {
      background: linear-gradient(135deg, #55efc4 0%, #00b894 100%);
      color: white;
      box-shadow: 0 4px 15px rgba(0, 184, 148, 0.4);
    }

    /* 更新容器 */
    .update-container {
      background: var(--card-bg);
      border-radius: 16px;
      box-shadow: var(--shadow);
      margin-bottom: 16px;
      overflow: hidden;
      border: 1px solid rgba(120, 130, 240, 0.1);
      animation: slideIn 0.5s ease 0.4s backwards;
    }

    .update-header {
      background: var(--light-bg);
      padding: 12px 16px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-bottom: 1px solid var(--border-color);
    }

    .update-header h2 {
      font-size: 1rem;
      font-weight: 600;
      color: var(--text-main);
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .update-header h2 i {
      color: #4e6ef2;
    }

    .update-tag {
      background: rgba(78, 110, 242, 0.1);
      color: #4e6ef2;
      padding: 4px 8px;
      border-radius: 8px;
      font-size: 0.75rem;
      font-weight: 600;
    }

    .update-content {
      padding: 16px;
    }

    .update-date {
      font-weight: 600;
      color: #4e6ef2;
      margin-bottom: 12px;
      display: flex;
      align-items: center;
      gap: 6px;
      padding: 8px 0;
      border-bottom: 1px dashed #e0e0e0;
    }

    .update-text {
      margin: 8px 0;
      position: relative;
      padding-left: 16px;
      line-height: 1.5;
      color: var(--text-main);
      font-size: 0.95rem;
      white-space: pre-wrap;
      word-break: break-word;
    }

    .update-text::before {
      content: '•';
      position: absolute;
      left: 0;
      font-weight: bold;
      color: #4e6ef2;
      font-size: 1.2rem;
      line-height: 1;
    }

    /* 历史日志 */
    .history-container {
      background: var(--card-bg);
      border-radius: 16px;
      box-shadow: var(--shadow);
      margin-bottom: 16px;
      border: 1px solid rgba(120, 130, 240, 0.1);
      animation: slideIn 0.5s ease 0.5s backwards;
    }

    .history-header {
      background: var(--light-bg);
      padding: 12px 16px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-bottom: 1px solid var(--border-color);
      cursor: pointer;
      user-select: none;
    }

    .history-header:hover {
      opacity: 0.8;
    }

    .history-header h2 {
      font-size: 1rem;
      font-weight: 600;
      color: var(--text-main);
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .history-header h2 i {
      color: #4e6ef2;
    }

    .toggle-history {
      background: none;
      border: none;
      color: var(--text-secondary);
      cursor: pointer;
      font-weight: 500;
      display: flex;
      align-items: center;
      gap: 4px;
      font-size: 0.85rem;
      transition: color 0.2s ease;
    }

    .toggle-history:hover {
      color: #4e6ef2;
    }

    .history-content {
      padding: 0 16px;
      max-height: 0;
      overflow: hidden;
      transition: max-height 0.4s ease, padding 0.4s ease;
    }

    .history-content.expanded {
      max-height: 60vh;
      overflow-y: auto;
      padding: 16px;
      scrollbar-width: thin;
      scrollbar-color: #4e6ef2 #f0f0f0;
    }

    .history-content.expanded::-webkit-scrollbar {
      width: 6px;
    }

    .history-content.expanded::-webkit-scrollbar-track {
      background: #f0f0f0;
      border-radius: 4px;
    }

    .history-content.expanded::-webkit-scrollbar-thumb {
      background: #4e6ef2;
      border-radius: 4px;
    }

    .history-content.expanded::-webkit-scrollbar-thumb:hover {
      background: #3a56d0;
    }

    .history-item {
      margin-bottom: 16px;
      padding-bottom: 16px;
      border-bottom: 1px dashed var(--border-color);
    }

    .history-item:last-child {
      border-bottom: none;
      margin-bottom: 0;
      padding-bottom: 0;
    }

    .history-date {
      font-weight: 600;
      color: var(--text-main);
      margin-bottom: 8px;
      display: flex;
      align-items: center;
      gap: 4px;
      font-size: 0.9rem;
      background: rgba(78, 110, 242, 0.05);
      padding: 6px 10px;
      border-radius: 6px;
    }

    .history-text {
      margin: 8px 0;
      padding-left: 16px;
      line-height: 1.4;
      color: var(--text-secondary);
      position: relative;
      font-size: 0.9rem;
      white-space: pre-wrap;
      word-break: break-word;
    }

    .history-text::before {
      content: '•';
      position: absolute;
      left: 0;
      color: #4e6ef2;
      font-weight: bold;
      font-size: 1.2rem;
      line-height: 1;
    }

    /* 按钮组 */
    .button-group {
      display: flex;
      flex-direction: column;
      gap: 10px;
      margin-bottom: 16px;
    }

    .button {
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;
      padding: 14px 28px;
      text-align: center;
      font-size: 1rem;
      border: none;
      border-radius: 12px;
      text-decoration: none;
      background: var(--primary-gradient);
      color: white;
      font-weight: 600;
      transition: all 0.3s ease;
      box-shadow: var(--shadow);
      position: relative;
      overflow: hidden;
      cursor: pointer;
    }

    .button i {
      font-size: 1rem;
    }

    .button::after {
      content: '';
      position: absolute;
      top: -50%;
      left: -50%;
      width: 200%;
      height: 200%;
      background: rgba(255, 255, 255, 0.1);
      transform: rotate(30deg);
      transition: all 0.6s ease;
      pointer-events: none;
    }

    .button:hover {
      transform: translateY(-3px);
      box-shadow: var(--shadow-hover);
    }

    .button:hover::after {
      transform: rotate(30deg) translate(20%, 20%);
    }

    .button:active {
      transform: scale(0.95);
    }

    /* 错误状态 */
    .error-state {
      text-align: center;
      padding: 40px 20px;
      color: var(--text-main);
    }

    .error-icon {
      font-size: 64px;
      margin-bottom: 20px;
      color: var(--error-color);
    }

    .error-text {
      font-size: 16px;
      line-height: 1.6;
      margin-bottom: 20px;
    }

    .retry-button {
      background: var(--primary-gradient);
      color: white;
      padding: 12px 30px;
      border-radius: 12px;
      border: none;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
      font-size: 14px;
      box-shadow: var(--shadow);
    }

    .retry-button:hover {
      transform: translateY(-2px);
      box-shadow: var(--shadow-hover);
    }

    .retry-button:active {
      transform: scale(0.95);
    }

    /* 装饰元素 */
    .decoration {
      position: absolute;
      z-index: 0;
      pointer-events: none;
    }

    .decoration.circle {
      width: 120px;
      height: 120px;
      border-radius: 50%;
      background: rgba(107, 45, 216, 0.05);
      top: 10%;
      left: 10%;
    }

    .decoration.square {
      width: 80px;
      height: 80px;
      transform: rotate(45deg);
      background: rgba(78, 110, 242, 0.05);
      bottom: 10%;
      right: 10%;
    }

    /* 响应式 */
    @media (max-width: 768px) {
      body {
        padding: 12px;
      }

      .container {
        max-width: 100%;
        border-radius: 20px;
      }

      .header {
        padding: 20px 15px;
      }

      .header h1 {
        font-size: 1.3rem;
      }

      .header-icon {
        font-size: 40px;
      }

      .version-comparison {
        flex-direction: row;
        flex-wrap: nowrap;
        gap: 10px;
        padding: 12px;
        margin-top: 6px;
        overflow-x: auto;
      }

      .version-card {
        min-width: 45%;
        padding: 26px 12px 12px;
      }

      /* 移动端减弱呼吸动效 */
      .version-card.latest-version {
        animation: gradientAnimation 6s ease infinite;
      }

      .version-status {
        top: 5px;
        right: 5px;
        padding: 2px 5px;
        font-size: 0.6rem;
      }

      .version-number {
        font-size: 1.1rem;
      }

      .version-card.latest-version .version-number {
        font-size: 1.2rem;
      }

      .update-header h2, .history-header h2 {
        font-size: 0.9rem;
      }

      .button {
        padding: 12px 24px;
        font-size: 0.95rem;
      }

      .history-content.expanded {
        max-height: 50vh;
        -webkit-overflow-scrolling: touch;
      }
    }

    @media (max-width: 380px) {
      .header h1 {
        font-size: 1.2rem;
      }

      .version-number {
        font-size: 1rem;
      }

      .version-card.latest-version .version-number {
        font-size: 1.1rem;
      }

      .button {
        padding: 11px;
        font-size: 0.9rem;
      }
    }
  </style>
</head>
<body>
  <div class="decoration circle"></div>
  <div class="decoration square"></div>

  <div id="loading" class="loading-wrapper">
    <div class="loading-spinner"></div>
    <div class="loading-text"><i class="fas fa-search"></i> 正在检查更新...</div>
  </div>

  <div class="container" id="container">
    <div class="header">
      <div class="header-icon"><i class="fas fa-book"></i></div>
      <h1>大灰狼书源更新</h1>
      <p>请使用阅读测试版，不支持正式版</p>
    </div>

    <div class="version-comparison">
      <div class="version-card current-version">
        <div class="version-status status-outdated" id="currentStatus">待检查</div>
        <h3><i class="fas fa-cube"></i> 当前版本</h3>
        <div class="version-number" id="currentVersion">-</div>
        <div class="version-date">您的当前版本</div>
      </div>

      <div class="version-indicator update-needed" id="versionIndicator" style="display: none;">
        <i class="fas fa-arrow-right"></i>
      </div>

      <div class="version-card latest-version">
        <div class="version-status status-latest" id="latestStatus">最新版本</div>
        <h3><i class="fas fa-star"></i> 最新版本</h3>
        <div class="version-number" id="latestVersion">-</div>
        <div class="version-date">可用最新版本</div>
      </div>
    </div>

    <div class="content-container">
      <div class="status-alert" id="statusAlert" style="display: none;"></div>

      <div id="latestLogContainer" style="display: none;">
        <div class="update-container">
          <div class="update-header">
            <h2><i class="fas fa-bolt"></i> 最新更新</h2>
            <div class="update-tag">最新发布</div>
          </div>
          <div class="update-content">
            <div class="update-date" id="latestLogDate"></div>
            <div class="update-text" id="latestLogContent"></div>
          </div>
        </div>
      </div>

      <div class="button-group" id="buttonGroup" style="display: none;"></div>

      <div class="history-container" id="logs" style="display: none;">
        <div class="history-header" onclick="toggleLogs()">
          <h2><i class="fas fa-history"></i> 历史更新 <span id="historyCount"></span></h2>
          <button class="toggle-history" id="toggleButton">
            <span id="toggleText">展开历史</span>
            <i class="fas fa-chevron-down" id="toggleIcon"></i>
          </button>
        </div>
        <div class="history-content" id="logList"></div>
      </div>
    </div>
  </div>

  <script>
  let logsCollapsed = true;

  function toggleLogs() {
    logsCollapsed = !logsCollapsed;
    const logList = document.getElementById('logList');
    const toggleText = document.getElementById('toggleText');
    const toggleIcon = document.getElementById('toggleIcon');

    if (logsCollapsed) {
      logList.classList.remove('expanded');
      toggleText.textContent = '展开历史';
      toggleIcon.className = 'fas fa-chevron-down';
    } else {
      logList.classList.add('expanded');
      toggleText.textContent = '收起历史';
      toggleIcon.className = 'fas fa-chevron-up';
    }
  }

  (async function() {
    const loading = document.getElementById('loading');
    const container = document.getElementById('container');
    const currentVersion = document.getElementById('currentVersion');
    const latestVersion = document.getElementById('latestVersion');
    const currentStatus = document.getElementById('currentStatus');
    const latestStatus = document.getElementById('latestStatus');
    const versionIndicator = document.getElementById('versionIndicator');
    const statusAlert = document.getElementById('statusAlert');
    const buttonGroup = document.getElementById('buttonGroup');
    const latestLogContainer = document.getElementById('latestLogContainer');
    const latestLogDate = document.getElementById('latestLogDate');
    const latestLogContent = document.getElementById('latestLogContent');
    const logsContainer = document.getElementById('logs');
    const logList = document.getElementById('logList');
    const historyCount = document.getElementById('historyCount');

    const localVer = '${String(localVersion)}';

    // 统一的服务器配置 - 方便维护
    const serverConfig = {
      main: {
        name: '主线路',
        icon: 'rocket',
        baseUrl: 'https://sy.dashabi.tk',
        downloadPath: '/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup1: {
        name: '备用线路1',
        icon: 'box',
        baseUrl: 'https://api.langge.cf',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup2: {
        name: '备用线路2',
        icon: 'satellite',
        baseUrl: 'https://20.langge.tk',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup3: {
        name: '备用线路3',
        icon: 'link',
        baseUrl: 'http://219.154.201.122:5006',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup4: {
        name: '备用线路4',
        icon: 'bolt',
        baseUrl: 'https://v2.czyl.cf',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup5: {
        name: '备用线路5',
        icon: 'globe',
        baseUrl: 'https://v7.czyl.cf',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      },
      backup6: {
        name: '备用线路6',
        icon: 'broadcast-tower',
        baseUrl: 'https://v8.czyl.cf',
        downloadPath: '/sy/download/%E5%AE%89%E5%8D%93%E9%98%85%E8%AF%BBapp-%E5%A4%A7%E7%81%B0%E7%8B%BC%E8%9E%8D%E5%90%884.0(%E5%AE%8C%E5%85%A8%E7%89%88).json'
      }
    };

    // 版本比较函数
    function compareVersions(vs) {
      const normalize = (v) => {
        return v.split('.').map(n => {
          const num = parseInt(n, 10);
          return isNaN(num) ? 0 : num;
        });
      };

      const parts1 = normalize(localVer);
      const parts2 = normalize(vs);
      const maxLength = Math.max(parts1.length, parts2.length);

      for (let i = 0; i < maxLength; i++) {
        const num1 = parts1[i] || 0;
        const num2 = parts2[i] || 0;
        if (num1 > num2) return 1;
        if (num1 < num2) return -1;
      }
      return 0;
    }

    async function fetchVersionData() {
      // 使用统一配置中除主线路外的备用线路进行版本检查
      const serversToCheck = Object.values(serverConfig).filter(s => s.baseUrl.includes('czyl.cf') || s.baseUrl.includes('langge.tk') || s.baseUrl.includes('langge.cf') || s.baseUrl.includes('219.154'));

      for (const server of serversToCheck) {
        try {
          const response = await fetch(server.baseUrl + '/version', { timeout: 2000 });
          if (response.ok) {
            return await response.json();
          }
        } catch (e) {
          console.warn(\`接口失败：\${server.baseUrl}\`, e);
        }
      }
      throw new Error('所有更新接口都请求失败');
    }

    function showError(message) {
      loading.innerHTML = \`
        <div class="error-state">
          <div class="error-icon"><i class="fas fa-exclamation-triangle"></i></div>
          <div class="error-text">\${message}</div>
          <button class="retry-button" onclick="location.reload()"><i class="fas fa-redo"></i> 重试</button>
        </div>
      \`;
    }

    try {
      const data = await fetchVersionData();
      const cloudVersion = String(data.version3);
      const updateLog = data.update_log || {};

      // 显示版本信息
      currentVersion.textContent = \`v\${localVer}\`;
      latestVersion.textContent = \`v\${cloudVersion}\`;

      // 处理日志
      const logEntries = Object.entries(updateLog);
      if (logEntries.length > 0) {
        // 显示最新日志
        const [latestDate, latestContent] = logEntries[0];
        latestLogDate.innerHTML = \`<i class="fas fa-calendar-alt"></i> \${latestDate}\`;
        latestLogContent.textContent = latestContent;
        latestLogContainer.style.display = 'block';

        // 显示历史日志
        if (logEntries.length > 1) {
          const historyLogs = logEntries.slice(1);
          historyCount.textContent = \`(\${historyLogs.length}条)\`;
          logList.innerHTML = historyLogs.map(([date, content]) => \`
            <div class="history-item">
              <div class="history-date">
                <i class="fas fa-calendar-day"></i>
                <span>\${date}</span>
              </div>
              <div class="history-text">\${content}</div>
            </div>
          \`).join('');
          logsContainer.style.display = 'block';
        }
      }

      // 检查更新状态
      const compareResult = compareVersions(cloudVersion);

      // 显示版本指示器
      versionIndicator.style.display = 'flex';

      if (compareResult === -1) {
        // 需要更新
        currentStatus.textContent = '待更新';
        currentStatus.className = 'version-status status-outdated';
        versionIndicator.className = 'version-indicator update-needed';
        versionIndicator.innerHTML = '<i class="fas fa-arrow-right"></i>';

        // 使用统一配置生成下载按钮
        buttonGroup.innerHTML = Object.values(serverConfig).map(server => {
          const fullUrl = server.baseUrl + server.downloadPath;
          return \`
            <a href="yuedu://booksource/importonline?src=\${encodeURIComponent(fullUrl)}" class="button">
              <i class="fas fa-\${server.icon}"></i>
              <span>\${server.name}</span>
            </a>
          \`;
        }).join('');
        buttonGroup.style.display = 'flex';
      } else {
        // 已是最新版本
        currentStatus.textContent = '最新';
        currentStatus.className = 'version-status status-latest';
        versionIndicator.className = 'version-indicator is-latest';
        versionIndicator.innerHTML = '<i class="fas fa-check"></i>';

        statusAlert.className = 'status-alert up-to-date';
        statusAlert.innerHTML = '<i class="fas fa-check-circle"></i> <div>您已是最新版本</div>';
        statusAlert.style.display = 'flex';
      }

      // 显示主容器，隐藏加载
      loading.style.display = 'none';
      container.style.display = 'block';

    } catch (err) {
      console.error('版本检查失败：', err);
      showError('<i class="fas fa-exclamation-circle"></i> 检查更新失败，请稍后重试<br><small>' + err.message + '</small>');
    }
  })();
  </script>
</body>
</html>
`;
    java.startBrowser(`data:text/html;base64,${java.base64Encode(html)}`, '大灰狼书源更新');
}

